SELECT *
FROM empleat e
JOIN tascaempleat te
USING (id_tascaempleat)
WHERE id_botigues = 2;

SELECT *
FROM clients
JOIN comandes
USING (dni_clients)
WHERE id_tipusrepatiment=1;


ALTER TABLE `pizzeria`.`comandes` 
CHANGE COLUMN `data/hora` `data` DATETIME NOT NULL ;

ALTER TABLE `pizzeria`.`comandes` 
CHANGE COLUMN `data` `data` DATE NOT NULL ;

SELECT *
FROM comandes
JOIN pizzes;


SELECT *
FROM categoria
WHERE id_categoria=2;


SELECT nomcategoria, nom, descripcio, preu
FROM categoria c
JOIN  pizzes p
ON c.id_categoria=p.id_categoria;


SELECT nom, descripcio, preu
FROM hamburgueses
UNION
SELECT nom, descripcio, preu
FROM pizzes
UNION
SELECT nom, descripcio, preu
FROM begudes;

